      }
    };
  },
  mounted() {
    this.getFormData();
    window.addEventListener('beforeunload', this.saveFormData);
  },
  beforeUnmount() {
    window.removeEventListener('beforeunload', this.saveFormData);
  },
  methods: {
    async saveFormData() {
      var result = this.formData;
      await set('VG-xxx-1', result);
    },
    async getFormData() {
      const savedData = await get('VG-xxx-1');
      if (savedData) {
        this.formData = savedData;
      }
    },
    submitForm() {
      this.getFormData();
    }
  }
